import { Text, StyleSheet, View, ActivityIndicator, FlatList } from 'react-native'
import React, { Component } from 'react'
import Umesh from './assets/components';



export default class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      polls: [],
      isLoading: true,
    };
  }

async getPolls() {
  try {
    const response = await fetch('https://hn.algolia.com/api/v1/search_by_date?tags=story&page=0');
    const json = await response.json();
    this.setState({polls: json.hits});
  } catch (error) {
    console.warn(error);
  } finally {
    this.setState({isLoading: false});

  }
}

componentDidMount() {
  this.getPolls();
  console.warn('api called')
}


  // componentDidMount() {
  //   this.timer = setInterval(()=> this.getItems(), 1000);
  // }

  // componentWillUnmount() {
  //   this.timer = null; // here...
  // }

  // getItems() {
  //   fetch(this.getEndpoint('api url endpoint'))
  //     .then(result => result.json())
  //     .then(result => this.setState({ items: result }));
  // }
  render() {
    const {polls, isLoading} = this.state;

    return (
      <View style={{flex: 1, padding: 24}}>
        {isLoading ? (
          <ActivityIndicator />
        ) : (
          <FlatList
            data={polls}
            keyExtractor={({id}) => id}
            renderItem={({item}) => (
              <Text>
                {item.title}, {item.author}
              </Text>
            )}
          />
        )}
      </View>
    );
  }
}


const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});
